    private void jBuCadastroActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBuCadastroActionPerformed
        
        CpfDAO cpdao = new CpfDAO();
       
        if(cpdao.checkCPFCProf(txtCPF.getText())){
            JOptionPane.showMessageDialog(null, "CPF JÁ CADASTRADO");
        }
        else{
        
        professores prof = new professores();
        ProfessoresDAO dao = new ProfessoresDAO();

        prof.setNome(txtNome.getText());
        prof.setCPF(txtCPF.getText());
        prof.setEndereco(txtEnd.getText());
        prof.setTelefone(Integer.parseInt(txtTel.getText()));
        prof.setIdade(Integer.parseInt(txtIda.getText()));
        prof.setSalario(Float.parseFloat(txtSal.getText()));
        prof.setHr_Trabalhadas(Float.parseFloat(txtHrTrab.getText()));
        dao.create(prof);

        txtNome.setText("");
        txtCPF.setText("");
        txtEnd.setText("");
        txtTel.setText("");
        txtIda.setText("");
        txtSal.setText("");
        txtHrTrab.setText("");
        
        }

        // TODO add your handling code here:
    }//GEN-LAST:event_jBuCadastroActionPerformed

