    private void readJTableForDesc(String nome) {
        DefaultTableModel modelo = (DefaultTableModel) jTProdutos.getModel();
        modelo.setNumRows(0);
        ProfessoresDAO prodao = new ProfessoresDAO();

        for (professores pro : prodao.readForNome(nome)) {

            modelo.addRow(new Object[]{
                pro.getIdProf(),
                pro.getNome(),
                pro.getCPF(),
                pro.getEndereco(),
                pro.getTelefone(),
                pro.getIdade(),
                pro.getSalario(),
                pro.getHr_Trabalhadas()
            });
        }
        
     }
   
